import React, { useState } from 'react'
import axios from 'axios';
import { toast } from 'react-toastify';

const Form = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    skills: '',
    message: ''
    })

    const handleChange = (e) =>{
      const { name, value } = e.target
      setFormData({
        ...formData,
        [name]: value.trimStart()
      })
    }

    const handleSubmit = async(e) => {
      e.preventDefault()

      try{
        const result = await axios.post('http://localhost:3005/receive',formData)
        console.log(result)
        toast.success(result.data.message)
      }catch(err){
        console.log(err)
      }
    }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label for="name">Name:</label>
        <input type="text" id="name" name="name" onChange={handleChange} value={formData.name} required /><br />

        <label for="email">Email:</label>
        <input type="email" id="email" name="email" onChange={handleChange} value={formData.email} required /><br />

        <label for="phone">Phone:</label>
        <input type="tel" id="phone" name="phone" onChange={handleChange} value={formData.phone}required /><br />

        <label for="skills">Skills:</label>
        <textarea id="skills" name="skills" onChange={handleChange} value={formData.skills} required></textarea><br />

        <label for="message">Why should we hire you?</label>
        <textarea id="message" name="message" onChange={handleChange} value={formData.message} required></textarea><br />

        <button type="submit">Submit</button>
      </form>
    </>
  )
}

export default Form
